function comprobar_resp(){
    var flag = true;
    var preg_mal = 0;
    var preg_sin = 0;
    for(var i=1;i<=preguntas;i++){
        elemento = document.querySelector('input[name="'+i+'"]:checked')
        //console.log(i)
        if(respuestas[i-1] ==="N/A"){
            //console.log("hola")
            pregunta_especial = probar_cuadros()
            if(pregunta_especial === -1){
                preg_sin +=1;
                document.getElementById('mensaje'+i).innerHTML = ''
                document.getElementById('mensaje0').innerHTML = ''
                document.getElementById('mensaje'+i).innerHTML += '<p>No respondiste esta pregunta</p>'
                document.getElementById('mensaje0').innerHTML += '<p>Revisa tus respuestas, hay '+preg_sin+' preguntas sin responder</p>'
                flag = false
            }
            else if(pregunta_especial === 0){
                preg_mal += 1;
                //console.log("esta mal")
            }
        }
        else if (elemento === null){
            preg_sin += 1;
            document.getElementById('mensaje'+i).innerHTML = ''
            document.getElementById('mensaje0').innerHTML = ''
            document.getElementById('mensaje'+i).innerHTML += '<p>No respondiste esta pregunta</p>'
            document.getElementById('mensaje0').innerHTML += '<p>Revisa tus respuestas, hay '+preg_sin+' preguntas sin responder</p>'
            flag = false
            }
        else if(elemento.value !== respuestas[i-1]){
                preg_mal +=1;
            }
    }
    nombre = document.getElementById("inombre").value
    if (nombre == ""){
        document.getElementById('mensaje0').innerHTML += '<p>Olvidaste poner tu nombre</p>'
        flag = false
    }
    if(flag){
        if(preg_mal == 0){
            var URL = 'assets/Secondary_pages/bien.html?nombre='+encodeURIComponent(nombre);
            window.location.href = URL;
        }
        else {
            var URL = 'assets/Secondary_pages/mal.html?nombre='+ encodeURIComponent(nombre) + "&preg_mal=" + encodeURIComponent(preg_mal);
            window.location.href = URL;
        }
    }
    else{
        window.scrollTo(0, 0)
    }
}
