function new_qest(texto,enunciado,a,b,c,d,respuesta,imagen,link){
    container = document.getElementById('cuerpo')
    num = preguntas+1;
    if(imagen ===""){
        clase_i = "respuestas_s";
    }
    else{
        clase_i = "respuestas_c";
        imagen = "assets/img/"+imagen
    }
    if(link){
        links = link.URL
        textos = link.texto
        pos = link.pos
        agregado = 0
        for(var i = 0;i<links.length;i++){
            string_a_agregar = '<a href="'+links[i]+'">'+textos[i]+'</a>'
            enunciado = [enunciado.slice(0, agregado+pos[i]), string_a_agregar, enunciado.slice(agregado+pos[i])].join('');
            agregado += string_a_agregar.length
        }
    }
    if(texto.includes("youtube.com")){
        //console.log(texto)
        texto = '<iframe width="100%" height="500vw" src="'+texto+'"> </iframe>'

    }
    code1 = '<div class="pregunta">'+'<div class="texto">'+texto+'</div>'+'<div '+'class = "enunciado"'+' >'+num+'. '+enunciado+'</div><div '+' id=mensaje'+num+''+' class="mensaje"></div><div class="'+clase_i+'">'+'<div class="respuestas">'

    code1+='<div class="respuesta respuesta_txt"><input onclick="pres('+num+')"'+' id=a'+num+''+' type="radio" name="'+num+'" value="a"><label '+'width="100%" '+'for="a'+num+'">'+a+'</label>'+'</div><br>'

    code1+='<div class="respuesta respuesta_txt">'+'<input onclick="pres('+num+')"'+' id=b'+num+''+' type="radio" name="'+num+'" value="b"><label for="b'+num+'">'+b+'</label>'+'</div><br>'

    if (c != "")code1+='<div class="respuesta respuesta_txt">'+'<input onclick="pres('+num+')"'+' id=c'+num+''+' type="radio"name="'+num+'" value="c"><label for="c'+num+'">'+c+'</label>'+'</div><br>'

    if(d != "")code1+='<div class="respuesta respuesta_txt">'+'<input onclick="pres('+num+')"'+' id=d'+num+''+' type="radio" name="'+num+'" value="d"><label for="d'+num+'">'+d+'</label>'+'</div>'

    code1+='</div>'+'<div class="imagen"><img width="100%" src="'+imagen+'"alt=""></div></div></div>';

    container.innerHTML += code1;
    //container.innerHTML += code2;
    preguntas += 1
    respuestas.push(respuesta)
}

function pres(num){
    elemento = document.getElementById('mensaje'+num)
    elemento.innerHTML = ""
}

function shuffle(array) {
    for (var i = array.length - 1; i > 0; i--) {
        var j = Math.floor(Math.random() * (i + 1));
        var temp = array[i];
        array[i] = array[j];
        array[j] = temp;
    }
}

function preg_esp(enunciado){
    preguntas += 1
    container = document.getElementById('cuerpo')
    code1 = '<div id="pregunta-esp">'+'<div class = "enunciado"'+' >'+preguntas+'. '+enunciado+'</div><div '+' id=mensaje'+preguntas+''+' class="mensaje"></div>'

    code1+= '<div id="container">'

    part_der = '<div id="derecha">'
    part_izq = '<div id="izquierda">'
    for(var i = 1;i<=5;i++){
        part_der += '<div id="z'+i+'" ondrop="drop(event)" ondragover="allowDrop(event)" ondragenter="dragEnter(event)" ondragleave="dragLeave(event)" class="vacio"></div>'
        part_izq += '<div id="i'+i+'" class="cuadro negrilla">'+izquierdos[i-1][0]+'</div>'
    }
    //pr = document.getElementById("pregunta-esp")
    code1 += part_izq+'</div>'
    code1 += part_der+'</div>'+'</div>'
    code1 += '<div id="cont_resp">'+respuestas_esp.join('')+'</div>'
    container.innerHTML += code1+'</div>'
    respuestas.push("N/A")
}

//change two divs of position
function change(source,target){
    src_class = source.className
    src_id = source.id
    src_cont = source.innerHTML

    source.setAttribute("class", target.className+" ");
    source.innerHTML = target.innerHTML
    source.setAttribute("ondrop", "drop(event)")
    source.setAttribute("ondragenter", "dragEnter(event)")
    source.setAttribute("ondragleave", "dragLeave(event)")
    source.setAttribute("ondragover", "allowDrop(event)")
    source.removeAttribute("draggable");
    source.removeAttribute("ondragstart");

    target.setAttribute("draggable", "true");
    target.setAttribute("ondrag", "on_drag_2()");
    target.setAttribute("ondragstart", "dragStart(event)");
    target.setAttribute("class", src_class);
    target.innerHTML = src_cont
    target.removeAttribute("ondrop");
    target.removeAttribute("ondragenter");
    target.removeAttribute("dragleave");
    target.removeAttribute("ondragover");
}

//shuffle an array
function shuffle(array) {
    for (var i = array.length - 1; i > 0; i--) {
        var j = Math.floor(Math.random() * (i + 1));
        var temp = array[i];
        array[i] = array[j];
        array[j] = temp;
    }
}

//drag events
var source = new Object();
//strat draggable element
function dragStart(event) {
    event.dataTransfer.setData("Text", event.target.id);
    source = event.target.id
    //console.log(source)
}

//dragged element enter to drag-box
function dragEnter(event) {
    //console.log("estoy en "+event.target.id)
    //console.log(document.getElementById(event.target.id))
    document.getElementById(event.target.id).style.border = "3px dotted red";
    //console.log(document.getElementById("d5"))

}

//draggable element leave a drag-box
function dragLeave(event) {
    //console.log("leave")
    document.getElementById(event.target.id).style.border = "";
}

//drag-element is inside a possible target
function allowDrop(event) {
    //console.log("lo puedo_dejar_aqui")
    event.preventDefault();
}

//drop action
function drop(event) {
    document.getElementById(event.target.id).style.border = "";
    target = document.getElementById(event.target.id)
    source = document.getElementById(source)

    if(target !== null && source !== null){
        change(source,target)
    }
    event.preventDefault();
}

//while dragging
function on_drag_2(){
    var y = event.clientY; //get Y cursor cordinate
    if(y<screen.height*.15)
        window.scrollBy(0, -3);
    else if(y>screen.height*.85)
        window.scrollBy(0, 3);
}
